import { Link, useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import "./singlePost.css";

export default function SinglePost() {
  const { id } = useParams(); // Get the post ID from the URL
  const [post, setPost] = useState(null); // State to store the post data

  useEffect(() => {
    const fetchPost = async () => {
      try {
        // Fetch the post using the id from the API
        
        const response = await axios.get(`/api/blogapi/get-post-list/?posts_id=${id}`);
        const postData = response.data.data[0];
        console.log(`/api/blogapi/get-post-list/?posts_id=${id}`);

        setPost(postData); // Set the post data
      } catch (error) {
        console.error("Failed to fetch post:", error);
      }
    };

    fetchPost();
  }, [id]);

  // If the post is not loaded yet, show a loading message
  if (!post) return <div>Loading...</div>;

  return (
    <div className="singlePost">
      <div className="singlePostWrapper">
        <img
          className="singlePostImg"
          src={`http://127.0.0.1:8000${post.image}`} // Prepend the base URL to the image path
          alt={post.title}
        />
        <h1 className="singlePostTitle">
          {post.title}
          <div className="singlePostEdit">
            <i className="singlePostIcon far fa-edit"></i>
            <i className="singlePostIcon far fa-trash-alt"></i>
          </div>
        </h1>
        <div className="singlePostInfo">
          <span>
            Author:
            <b className="singlePostAuthor">
              <Link className="link" to={`/posts?username=${post.author || "Unknown"}`}>
                {post.author || "Unknown"}
              </Link>
            </b>
          </span>
          <span>{new Date(post.created_at).toDateString()}</span>
        </div>
        <p className="singlePostDesc">
          {post.content}
        </p>
      </div>
    </div>
  );
}
