import Post from "../post/Post";
import "./posts.css";

export default function Posts({ posts }) {
  return (
    <div className="posts">
      {posts.map((post) => (
        <Post
          key={post.id}
          img={post.image} // Assuming the image URL is in the `image` field
          title={post.title}
          desc={post.content} // Assuming the content is in the `content` field
          date={new Date(post.created_at).toLocaleDateString()} // Formatting date
        />
      ))}
    </div>
  );
}
